\begin{equation*}
\left\{ \begin{pmatrix}
a & b \\
0 & 1
\end{pmatrix} \in \mathrm{SL}_2(\mathbb{Z}) : a \equiv 1 \pmod{N}, b \equiv 0 \pmod{N} \right\}.
\end{equation*}

\begin{lemma}
\label{lem:congruence}
Let $N \geq 3$ be an integer.
Let $f \in S_k(\Gamma_1(N))$ be a normalized Hecke eigenform.
Let $p$ be a prime not dividing $N$.
Let $\alpha_p$ and $\beta_p$ be the roots of the Hecke polynomial $X^2 - a_p(f) X + p^{k-1}$.
Then
\begin{equation*}
\alpha_p \equiv \beta_p \equiv 1 \pmod{N}.
\end{equation*}
\end{lemma}

\begin{proof}
Let $\gamma = \begin{pmatrix}
a & b \\
0 & 1
\end{pmatrix} \in \Gamma_1(N)$.
Then
\begin{equation*}
\begin{pmatrix}
a & b \\
0 & 1
\end{pmatrix}
\begin{pmatrix}
p & 0 \\
0 & p
\end{pmatrix}
\begin{pmatrix}
a & b \\
0 & 1
\end{pmatrix}^{-1}
=
\begin{pmatrix}
p & bp \\
0 & p
\end{pmatrix}.
\end{equation*}
Since $b \equiv 0 \pmod{N}$, we have
\begin{equation*}
\begin{pmatrix}
p & bp \\
0 & p
\end{pmatrix}
\equiv
\begin{pmatrix}
p & 0 \\
0 & p
\end{pmatrix}
\pmod{N}.
\end{equation*}
Therefore,
\begin{equation*}
a_p(f)
= p^{k-1} f|_k \begin{pmatrix}
p & 0 \\
0 & p
\end{pmatrix}
= p^{k-1} f|_k \begin{pmatrix}
p & bp \\
0 & p
\end{pmatrix}
= a_p(f|_k \gamma).
\end{equation*}
Since $f$ is a normalized Hecke eigenform, we have
\begin{equation*}
a_p(f)
= a_p(f|_k \gamma)
= \alpha_p(f|_k \gamma) + \beta_p(f|_k \gamma)
= \alpha_p + \beta_p.
\end{equation*}
Since $\alpha_p \beta_p = p^{k-1}$, we have
\begin{equation*}
\alpha_p \equiv \beta_p \equiv 1 \pmod{N}.
\end{equation*}
\end{proof}

