 import com.google.gson.Gson;
 import com.google.gson.GsonBuilder;
 import com.google.gson.reflect.TypeToken;

 public class JsonReader {

	public static List<JsonData> readJsonFile(String file) {
		List<JsonData> jsonDataList = new ArrayList<JsonData>();
		JSONParser parser = new JSONParser();
		try {
			Object obj = parser.parse(new FileReader(file));
			JSONArray jsonArray = (JSONArray) obj;
			Gson gson = new GsonBuilder().create();
			jsonDataList = gson.fromJson(jsonArray.toString(), new TypeToken<List<JsonData>>() {}.getType());
		} catch (FileNotFoundException e) {
			e.printStackTrace();
		} catch (IOException e) {
			e.printStackTrace();
		} catch (ParseException e) {
			e.printStackTrace();
		}
		return jsonDataList;
	}

	public static void main(String[] args) {
		List<JsonData> jsonDataList = readJsonFile("C:\\Users\\user\\Desktop\\json\\json.json");
		for (JsonData jsonData : jsonDataList) {
			System.out.println(jsonData.getTitle());
		}
	}

}

