from django.contrib.auth.decorators import login_required
from django.contrib.auth.models import User
from django.shortcuts import render, redirect
from django.urls import reverse
from django.utils.decorators import method_decorator
from django.views import View
from django.views.generic import CreateView, UpdateView, DeleteView
from django.views.generic.detail import DetailView
from django.views.generic.list import ListView

from .models import Post, Comment
from .forms import PostForm, CommentForm


class PostList(ListView):
    model = Post
    template_name = 'blog/post_list.html'
    context_object_name = 'posts'
    ordering = ['-created_at']
    paginate_by = 5


class PostDetail(DetailView):
    model = Post
    template_name = 'blog/post_detail.html'
    context_object_name = 'post'

    def get_context_data(self, **kwargs):
        context = super().get_context_data(**kwargs)
        context['form'] = CommentForm()
        return context


class PostCreate(CreateView):
    model = Post
    form_class = PostForm
    template_name = 'blog/post_form.html'

    def form_valid(self, form):
        form.instance.author = self.request.user
        return super().form_valid(form)

    def get_success_url(self):
        return reverse('blog:post_detail', kwargs={'pk': self.object.pk})


class PostUpdate(UpdateView):
    model = Post
    form_class = PostForm
    template_name = 'blog/post_form.html'

    def get_success_url(self):
        return reverse('blog:post_detail', kwargs={'pk': self.object.pk})


class PostDelete(DeleteView):
    model = Post
    template_name = 'blog/post_confirm_delete.html'

    def get_success_url(self):
        return reverse('blog:post_list')


class CommentCreate(CreateView):
    model = Comment
    form_class = CommentForm
    template_name = 'blog/comment_form.html'

    def form_valid(self, form):
        form.instance.author = self.request.user
        form.instance.post = Post.objects.get(pk=self.kwargs['post_pk'])
        return super().form_valid(form)

    def get_success_url(self):
        return reverse('blog:post_detail', kwargs={'pk': self.kwargs['post_pk']})


class UserProfileView(DetailView):
    model = User
    template_name = 'blog/user_profile.html'
    context_object_name = 'user'

    def get_context_data(self, **kwargs):
        context = super().get_context_data(**kwargs)
        context['posts'] = Post.objects.filter(author=self.object)
        context['following'] = self.request.user.is_following(self.object)
        return context


class UserProfileUpdate(UpdateView):
    model = UserProfile
    form_class = UserProfileForm
    template_name = 'blog/user_profile_form.html'

    def get_object(self, queryset=None):
        return self.request.user.userprofile

    def get_success_url(self):
        return reverse('blog:user_profile', kwargs={'pk': self.object.user.pk})


class FollowToggle(View):
    def post(self, request, *args, **kwargs):
        user_to_toggle = User.objects.get(pk=self.kwargs['pk'])
        if request.user.is_following(user_to_toggle):
            request.user.unfollow(user_to_toggle)
        else:
            request.user.follow(user_to_toggle)
        return redirect('blog:user_profile', pk=user_to_toggle.pk)


@method_decorator(login_required, name='dispatch')
class PostLikeToggle(View):
    def post(self, request, *args, **kwargs):
        post = Post.objects.get(pk=self.kwargs['pk'])
        if request.user.like_post(post):
            request.user.unlike_post(post)
        else:
            request.user.like_post(post)
        return redirect('blog:post_detail', pk=post.pk)


@method_decorator(login_required, name='dispatch')
class CommentLikeToggle(View):
    def post(self, request, *args, **kwargs):
        comment = Comment.objects.get(pk=self.kwargs['pk'])
        if request.user.like_comment(comment):
            request.user.unlike_comment(comment)
        else:
            request.user.like_comment(comment)
        return redirect('blog:post_detail', pk=comment.post.pk)


@method_decorator(login_required, name='dispatch')
class CommentDelete(DeleteView):
    model = Comment
    template_name = 'blog/comment_confirm_delete.html'

    def get_success_url(self):
        return reverse('blog:post_detail', kwargs={'pk': self.object.post.pk})

    def get_object(self, queryset=None):
        return Comment.objects.get(pk=self.kwargs['pk'])


