 #include<stdio.h>
 #include<stdlib.h>
 #include<string.h>

 struct node
 {
     char data[10];
     struct node *next;
 };

 void insert(struct node **start,char str[10])
 {
     struct node *ptr,*temp;
     ptr=(struct node*)malloc(sizeof(struct node));
     strcpy(ptr->data,str);
     ptr->next=NULL;
     if(*start==NULL)
     {
         *start=ptr;
     }
     else
     {
         temp=*start;
         while(temp->next!=NULL)
         {
             temp=temp->next;
         }
         temp->next=ptr;
     }
 }

 void display(struct node *start)
 {
     struct node *ptr;
     ptr=start;
     while(ptr!=NULL)
     {
         printf("%s\t",ptr->data);
         ptr=ptr->next;
     }
 }

 void search(struct node *start,char str[10])
 {
     struct node *ptr;
     ptr=start;
     int flag=0;
     while(ptr!=NULL)
     {
         if(strcmp(ptr->data,str)==0)
         {
             flag=1;
             break;
         }
         ptr=ptr->next;
     }
     if(flag==1)
         printf("\n%s is present in the list",str);
     else
         printf("\n%s is not present in the list",str);
 }

 void delete(struct node **start,char str[10])
 {
     struct node *ptr,*temp;
     ptr=*start;
     if(strcmp(ptr->data,str)==0)
     {
         *start=ptr->next;
         free(ptr);
     }
     else
     {
         while(ptr!=NULL && strcmp(ptr->data,str)!=0)
         {
             temp=ptr;
             ptr=ptr->next;
         }
         if(ptr==NULL)
         {
             printf("\n%s is not present in the list",str);
         }
         else
         {
             temp->next=ptr->next;
             free(ptr);
         }
     }
 }

 int main()
 {
     struct node *start=NULL;
     int ch;
     char str[10];
     while(1)
     {
         printf("\n1.Insert\n2.Display\n3.Search\n4.Delete\n5.Exit");
         printf("\nEnter your choice:");
         scanf("%d",&ch);
         switch(ch)
         {
             case 1:printf("\nEnter the string:");
                     scanf("%s",str);
                     insert(&start,str);
                     break;
             case 2:printf("\nThe list is:");
                     display(start);
                     break;
             case 3:printf("\nEnter the string to be searched:");
                     scanf("%s",str);
                     search(start,str);
                     break;
             case 4:printf("\nEnter the string to be deleted:");
                     scanf("%s",str);
                     delete(&start,str);
                     break;
             case 5:exit(0);
             default:printf("\nInvalid choice");
         }
     }
     return 0;
 }

