 struct node
 {
     char data[10];
     struct node *next;
 };

 void insert(struct node **start,char str[10])
 {
     struct node *ptr,*temp;
     ptr=(struct node*)malloc(sizeof(struct node));
     strcpy(ptr->data,str);
     ptr->next=NULL;
     if(*start==NULL)
     {
         *start=ptr;
     }
     else
     {
         temp=*start;
         while(temp->next!=NULL)
         {
             temp=temp->next;
         }
         temp->next=ptr;
     }
 }

 void display(struct node *start)
 {
     struct node *ptr;
     ptr=start;
     while(ptr!=NULL)
     {
         printf("%s\t",ptr->data);
         ptr=ptr->next;
     }
 }

 void search(struct node *start,char str[10])
 {
     struct node *ptr;
     ptr=start;
     int flag=0;
     while(ptr!=NULL)
     {
         if(strcmp(ptr->data,str)==0)
         {
             flag=1;
             break;
         }
         ptr=ptr->next;
     }
     if(flag==1)
         printf("\n%s is found",str);
     else
         printf("\n%s is not found",str);
 }

 void delete(struct node **start,char str[10])
 {
     struct node *ptr,*temp;
     ptr=*start;
     if(strcmp(ptr->data,str)==0)
     {
         *start=ptr->next;
         free(ptr);
     }
     else
     {
         while(ptr!=NULL && strcmp(ptr->data,str)!=0)
         {
             temp=ptr;
             ptr=ptr->next;
         }
         if(ptr==NULL)
         {
             printf("\n%s is not found",str);
         }
         else
         {
             temp->next=ptr->next;
             free(ptr);
         }
     }
 }

