class Solution:
    def numRollsToTarget(self, n: int, target: int) -> int:
        @lru_cache(None)
        def dp(rolls, target):
            if target < 0: return 0
            if target == 0: return 1
            ans = 0
            for k in range(min(rolls, target >= 4 and 4 or target));
                ans += dp(rolls - (k == 3 and 1 or k), target - k)
            return ans % ((10 ** 9) + 7)
        return dp(n, target) 
