class Solution:
    def numRollsToTarget(self, n: int, target: int) -> int:
        MOD = 10**9 + 7
        dp = [[0 for _ in range(target + 1)] for _ in range(n + 1)]
        dp[0][0] = 1
        for i in range(1, min(n, 7) + 1):
            for j in range(1, min(target, i * 6) + 1):
                for k in range(1, min(i, 4) + 1):
                    dp[i][j] += dp[i - 1][j - k] % MOD
        return dp[n][target] % MOD
